def print_welcome_screen():
   print(WELCOME_TO_HANGMAN)
   print(hangman_logo)

def display_messages(messages):
    for message in messages:
        print(message)
        time.sleep(0.5)

def get_game_difficulty():
    print('Choose your difficulty: \n 1. Easy \n 2. Hard \n You may also quit if you type \'quit\'.')
    while True:
        select_difficulty = input('Please choose: ').casefold()
        if select_difficulty == '1':
            display_messages(['Selected difficulty: Easy', 'You have 7 Lives.', 'Generating random 5 letter word.', 'Have Fun!'])
            return 1
        elif select_difficulty == '2':
            display_messages(['Selected difficulty: Hard', 'You have 3 lives.', 'Generating random >= 6 letter word', 'Have fun ripping your hair out!'])
            return 2
        elif select_difficulty == 'quit':
            display_messages(['You have decided to quit, farewell.'])
            sys.exit()
        else:
            print('Inappropriate input')
            continue


def get_word(return_difficulty_choice):                                                               
     word = ""
     word_search = ''
     import_random = []
     str1_collect = []
     str2_collect = []
     word_search_beta = open('countries-and-capitals.txt')
     for element in word_search_beta:
         str1 = element.split(' | ')[0]
         str2 = element.split(' | ')[1].split('\n')[0]
         str1_collect.append(str1)
         str2_collect.append(str2)
     import_random.extend(str1_collect)  
     import_random.extend(str2_collect) 
     while True:  
       while return_difficulty_choice == 1:
           word_search = random.choice(import_random)
           if len(word_search) < 6:
                word = word_search
                return word
           else:
                continue
       while return_difficulty_choice == 2:
           word_search = random.choice(import_random)
           if len(word_search) >= 6: 
               word = word_search
               return word
           else:
               continue


def get_nr_of_tries(return_difficulty_choice):
    nr_of_tries = 0
    if return_difficulty_choice == 1:
        nr_of_tries = 7
        return nr_of_tries
    elif return_difficulty_choice == 2:
        nr_of_tries = 3
        return nr_of_tries


def get_LETTERS(word):
    word_lower = word.lower()
    LETTERS = list(word_lower)
    return LETTERS


def get_letter(nr_of_tries,LETTERS,return_difficulty_choice):  
    letter_position = 0
    good_letter = set(())
    bad_letter = set(())
    win_counter = 0
    hidden_word = ['_ ' for i in range(len(LETTERS))]
    print(hidden_word)
    while True:
        if win_counter == len(LETTERS):
            print("YOU WON")
            print(winning_screen)
            break
        if nr_of_tries == 0:
            print("YOU LOST")
            print(game_over_screen)
            print("The word was ", LETTERS)
            break
        
        print("Please type a single letter")
        letter_inp = input()
        letter = letter_inp.casefold()
        if letter == 'quit':
            print("Seems like you got scared, hehe. Quitting game...")
            time.sleep(3)
            sys.exit
            break
        elif (letter.isalpha() == True or letter == ' ') and len(letter) == 1:
            if letter in good_letter:
                print("You have already guessed that letter")
                print("Letters that you've already tried: ")
                print(good_letter) 
                print(bad_letter)
                continue
            if letter in bad_letter:
                print("You have already tried that letter")
                print("Letters that you've already tried: ")
                print(good_letter) 
                print(bad_letter)
                continue
            if letter in LETTERS:
                count=LETTERS.count(letter)
                win_counter = win_counter + count
                for i in range(count):
                    letter_position = LETTERS.index(letter)
                    if letter_position == 0:
                        hidden_word[letter_position] = letter.upper()
                        print(hidden_word)
                        print("You've found something !")
                    else:
                        hidden_word[letter_position] = letter
                        print(hidden_word)
                        print("You've found something !")
            if letter not in good_letter and letter in LETTERS:
                good_letter.add(letter)
                print("You guessed that letter !")
            elif letter not in bad_letter:
                if letter not in LETTERS:
                    bad_letter.add(letter) 
                    nr_of_tries = nr_of_tries -1
                    if return_difficulty_choice == 1:
                        if nr_of_tries == 6:
                            print(gameplay1)
                        if nr_of_tries == 5:
                            print(gameplay2)
                        if nr_of_tries == 4:
                            print(gameplay3)
                        if nr_of_tries == 3:
                            print(gameplay4)
                        if nr_of_tries == 2:
                            print(gameplay5)
                        if nr_of_tries == 1:
                            print(gameplay6)
                        if nr_of_tries == 0:
                            print(gameplay7)
                    if return_difficulty_choice == 2:
                        if nr_of_tries == 2:
                            print(gameplay1)
                        if nr_of_tries == 1:
                            print(gameplay6)
                        if nr_of_tries == 0:
                            print(gameplay7)
                    print(hidden_word)
                    print("You didn't guess that letter...Try again !")
        else:
            print("That's gibberish.")
            

